import { useState } from 'react';
import Group from './Group';
import { months, calcButtonTextColor } from '../tools';
//import sessionCount from "./pages/Alarm/PomodoroPage";
import SettingsContext from ".pages/Alarm/SettingsContext";

export default function EditableUserProfile({
    stored,
    startEditCallback
}) {

    console.log()

    const buttonStyle = {
        backgroundColor: stored.color,
        color: calcButtonTextColor(stored.color)
    };
    const [sessionCount, setSessionCount] = useState(0);
    return <div>
        <Group>
            <h2>Name:</h2> {stored.name}
        </Group>
        <Group>
            <h2>Birthday:</h2> {months.getShortName(stored.month)} {stored.day}
        </Group>

        <Group>
        <SettingsContext.Provider value={settings}>
        showSettings,
        setShowSettings,
        workMinutes,
        breakMinutes,
        setWorkMinutes,
        setBreakMinutes,
        sessionCount,
        setSessionCount
        </SettingsContext.Provider>

            <h2>Total Completed Sessions:</h2> {sessionCount}
        </Group>

        <Group>
            <button
                style={buttonStyle}
                onClick={startEditCallback}
            >Edit</button>
        </Group>
    </div>
}
